inline void normall(){
	 pu=1;
    for(int i=1;i<=n;++i){
    	u=(pu+(pl<<1))%mod;
    	l=(pu+pl)%mod;
    	pu=u,pl=l;
    }
	printf("%lld\n",(u+(l<<1))%mod);
} 

inline void special(){
	pu=U[n/1000000],pl=L[n/1000000];
   	for(int i=(n/1000000)*1000000;i<=n;++i){
   		u=(pu+(pl<<1))%mod;
    	l=(pu+pl)%mod;
    	pu=u,pl=l;
	   }
	printf("%lld",(u+(l<<1))%mod);
}

